import './App.css';
import { io } from "socket.io-client";
import logo from "./assets/chat.png";
import { useState } from "react";

 const socket = io (`http://localhost:7000`);

function App() {

  const [step, setStep] = useState(0);


  const onCreateUser = () =>{
    setStep (prevStep => prevStep +1);
  };

  const onUserSelect =(key)=>{
    setStep(prevStep => prevStep +1);
  };
  return <div className = "App">
     <header className ="app-header">
       <img src={logo} alt=""  />
       <div className="app-name b-500 primaryColor">Chat</div>
     </header>

    <div className="chat-system">
      <div className="chat-box">

        {/* paso 1: pedir nombre de usuario o correo electr√≥nico */}
        {step === 0 ? (
          <div className='username-container '>
            <form onSubmit={onCreateUser} style={{display :"inline-block"}}>
              <h4 className='username-label'>Ingrese Usuario</h4>
              <input className='input'/>

            </form>

          </div>

        ): null }

        {/* Paso 2: mostrar todos los usuarios disponibles */}
        {step ===1 ?(
          <div> 
            <div className='online-users-header'>
              <div style={{margin: "0 10 px"}}>Usuarios Disponibles</div> 
            </div>
            <div > 
              <ul className='users-list'>
                <li onClick={ () => onUserSelect ('Yusdalis')}>
                  <span>Yusadalis</span>
                  <span className='new-message-count'> 2</span>
                </li>
                <li>
                  <span style={{textTransform:'capitalize'}}>Yusadalis</span>
                  <span className='new-message-count'> 3</span>
                </li>
              </ul>
            </div>

          </div>

        ):null }

       {/* Paso 3: selecciona el usuario y cambia a la ventana de chat */}


      </div>

    </div>


  </div> ;


}

export default App;
