import './App.css';
import { io } from "socket.io-client";
import logo from "./assets/chat.png";
import { useEffect, useState } from "react";
import CreateUser from './components/CreateUser';
import OnlineUsers from './components/OnlineUsers';
import MessagesControl from './components/MessagesControl';

 const socket = io (`http://localhost:7000`);

function App() {

  const [step, setStep] = useState(0);
  const [username, setUsername]= useState("");
  const [users,  setUsers]= useState ([]);


  const onCreateUser = () =>{
  console.log(username);

  socket.emit("new_user",username);

    setStep (prevStep => prevStep +1);
  };

  const onUserSelect =(key)=>{
    setStep(prevStep => prevStep +1);
  };

  useEffect(()=>{
    socket.on("new-user",(users)=>{
      console.log({users});
      setUsers(users);
    });
  }, []);

  return <div className = "App">
     <header className ="app-header">
       <img src={logo} alt=""  />
       <div className="app-name b-500 primaryColor">Chat</div>
     </header>

    <div className="chat-system">
      <div className="chat-box">

        {/* paso 1: pedir nombre de usuario o correo electr√≥nico */}
        {step === 0 ? (
          <CreateUser
          onCreateUser={onCreateUser}
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        ): null }

        {/* Paso 2: mostrar todos los usuarios disponibles */}
        {step === 1 ? (
          <OnlineUsers onUserSelect = {onUserSelect }
          users={users}
          username={username}
          />

        ): null }
       {/* Paso 3: selecciona el usuario y cambia a la ventana de chat */}

       {step === 2 ? (
            <MessagesControl />
          ) : null}

      </div>

    </div>


  </div> ;


}

export default App;
