import './App.css';
import { io } from "socket.io-client";
import logo from "./assets/chat.png";

 const socket = io (`http://localhost:7000`);

function App() {

  const [step, setStep] = useState(0);


  const onCreateUser = () =>{
    setStep (prevStep => prevStep +1);
  };

  return <div className = "App">
     <header className ="app-header">
       <img src={logo} alt=""  />
       <div className="app-name b-500 primaryColor">Chat</div>
     </header>

    <div className="chat-system">
      <div className="chat-box">

        {/* paso 1: pedir nombre de usuario o correo electr√≥nico */}
        { step === 0 ? (
         <div className="username-container">
         <form onSubmit={onCreateUser} style={{ display: "inline-block" }}>
           <h4 className="username-label">Enter username</h4>
         </form>
       </div>

      ) :  null}
        {/* Paso 2: mostrar todos los usuarios disponibles */}
        { step ===1 ? (
          <div> 
            <div className="online-users-header">
              <div>Usuarios Disponibles</div>

            </div>
          </div>



        ): null }
       {/* Paso 3: selecciona el usuario y cambia a la ventana de chat */}


      </div>

    </div>


  </div> ;


}

export default App;
